function TemperatureConverter(e,t){t=parseFloat(t);var n=document.getElementById("inputFahrenheit"),u=document.getElementById("inputCelsius"),i=document.getElementById("inputKelvin");"inputFahrenheit"==e&&(u.value=((t-32)/1.8).toFixed(2),i.value=((t-32)/1.8+273.15).toFixed(2)),"inputCelsius"==e&&(n.value=(1.8*t+32).toFixed(2),i.value=(t+273.15).toFixed(2)),"inputKelvin"==e&&(n.value=(1.8*(t-273.15)+32).toFixed(2),u.value=(t-273.15).toFixed(2))}function ClearValue(){document.getElementById("inputKelvin").value="",document.getElementById("inputFahrenheit").value="",document.getElementById("inputCelsius").value=""}